mapper.select:
		SELECT
			*
		FROM
			USER
		WHERE 1 = 1
			
    			AND id = #{id}
  			
  			
  				AND username = #{username}
  			
  			
  				AND password = #{password}
  			
  			
  				AND gender = #{gender}
  			
  			
  				AND mobile = #{mobile}
  			
  			
  				AND email = #{email}
  			
  			
  				AND score = #{score}
  			
  			
  				AND last_login_time = #{lastLoginTime}
